import React, { useState, useEffect } from 'react';
import ProductForm from '../components/ProductForm';
import ProductList from '../components/ProductList';
import AuthModal from '../components/AuthModal';
import { useApi } from '../hooks/useApi';

interface Product {
  id: number;
  title: string;
  description: string;
  price: number;
  imageUrl: string;
}

const AdminPage = () => {
  const [showAuthModal, setShowAuthModal] = useState(true);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [showForm, setShowForm] = useState(false);
  const { data: products, callApi: fetchProducts } = useApi<Product[]>();
  const { callApi: deleteProduct } = useApi();
  const { login, loading: authLoading } = useAdminAuth();

  // Vérifier l'authentification au montage
  useEffect(() => {
    const token = localStorage.getItem('adminToken');
    if (token) {
      setIsAuthenticated(true);
      setShowAuthModal(false);
      fetchProducts('/products?page=1');
    }
  }, []);

  const handleLogin = async (username: string, password: string) => {
    const result = await login(username, password);
    if (result?.token) {
      localStorage.setItem('adminToken', result.token);
      setIsAuthenticated(true);
      setShowAuthModal(false);
      fetchProducts('/products?page=1');
    }
  };

  const handleDelete = async (id: number) => {
    if (window.confirm('Êtes-vous sûr de vouloir supprimer ce produit?')) {
      await deleteProduct(`/products/${id}`, { method: 'DELETE' });
      fetchProducts('/products?page=1'); // Rafraîchir la liste
    }
  };

  if (!isAuthenticated) {
    return (
      <AuthModal
        show={showAuthModal}
        onLogin={handleLogin}
        loading={authLoading}
        onClose={() => setShowAuthModal(false)}
      />
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-3xl font-bold">Administration des Produits</h1>
        <button
          onClick={() => setShowForm(!showForm)}
          className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
        >
          {showForm ? 'Annuler' : 'Ajouter un Produit'}
        </button>
      </div>

      {showForm && (
        <div className="mb-12">
          <ProductForm 
            onSuccess={() => {
              setShowForm(false);
              fetchProducts('/products?page=1');
            }} 
          />
        </div>
      )}

      <ProductList 
        products={products || []} 
        adminMode={true}
        onDelete={handleDelete}
      />
    </div>
  );
};


const useAdminAuth = () => {
  const { callApi, ...state } = useApi<{ token: string }>();

  const login = async (username: string, password: string) => {
    return callApi('/auth/login', {
      method: 'POST',
      body: { username, password }
    });
  };

  return { login, ...state };
};

export default AdminPage;
