// src/pages/HomePage.tsx

import React, { useState, useEffect } from "react";
import ProductList from "../components/ProductList";
import ReviewSection from "../components/ReviewSection";
import RatingChart from "../components/RatingChart"; // This import is now correct
import { useApi } from "../hooks/useApi";

interface Product {
  id: number;
  title: string;
  description: string;
  price: number;
  imageUrl: string;
  averageRating?: number;
}

interface ProductListResponse {
  data: Product[];
  meta: {
    total: number;
    page: number;
    last_page: number;
  };
}

const HomePage = () => {
  const [selectedProduct, setSelectedProduct] = useState<Product | null>(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  // Hook pour récupérer les produits
  const { data: productsResponse, callApi: fetchProducts } =
    useApi<ProductListResponse>();

  // Hook pour récupérer la distribution des notes
  const { data: ratingDistribution, callApi: fetchRatingDistribution } =
    useApi<{ rating: number; count: number }[]>();

  // Charger les produits au montage et lors du changement de page
  useEffect(() => {
    fetchProducts(`/products?page=${currentPage}`);
  }, [currentPage]);

  // Mettre à jour le nombre total de pages
  useEffect(() => {
    if (productsResponse) {
      setTotalPages(productsResponse.meta.last_page);
    }
  }, [productsResponse]);

  // Charger la distribution des notes quand un produit est sélectionné
  useEffect(() => {
    if (selectedProduct) {
      fetchRatingDistribution(
        `/products/${selectedProduct.id}/ratings/distribution`
      );
    }
  }, [selectedProduct]);

  // Fonction pour changer de page
  const handlePageChange = (page: number) => {
    setCurrentPage(page);
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-8">Liste des Produits</h1>

      {/* Liste des produits avec pagination */}
      <ProductList
        products={productsResponse?.data || []}
        currentPage={currentPage}
        totalPages={totalPages}
        onPageChange={handlePageChange}
        onSelectProduct={setSelectedProduct}
      />

      {/* Section détaillée pour le produit sélectionné */}
      {selectedProduct && (
        <div className="mt-12 border-t pt-8">
          <h2 className="text-2xl font-bold mb-6">
            Détails et Avis pour {selectedProduct.title}
          </h2>

          <div className="flex flex-wrap gap-8">
            <div className="w-full lg:w-1/2">
              <div className="bg-white p-6 rounded-lg shadow-md">
                <div className="flex items-center mb-6">
                  <img
                    src={selectedProduct.imageUrl}
                    alt={selectedProduct.title}
                    className="w-32 h-32 object-contain mr-6"
                  />
                  <div>
                    <h3 className="text-xl font-bold">
                      {selectedProduct.title}
                    </h3>
                    <p className="text-gray-600 mt-2">
                      {selectedProduct.description}
                    </p>
                    <p className="text-2xl font-bold mt-2 text-blue-600">
                      {selectedProduct.price.toLocaleString()} MGA
                    </p>
                  </div>
                </div>

                <ReviewSection
                  productId={selectedProduct.id}
                  averageRating={selectedProduct.averageRating || 0}
                />
              </div>
            </div>

            <div className="w-full lg:w-2/5">
              <div className="bg-white p-6 rounded-lg shadow-md">
                <h3 className="text-xl font-bold mb-4">
                  Distribution des notes
                </h3>

                {ratingDistribution ? (
                  // Here, RatingChart expects the 'data' prop
                  <RatingChart data={ratingDistribution} />
                ) : (
                  <div className="flex justify-center items-center h-64">
                    <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
                  </div>
                )}

                <div className="mt-6">
                  <h4 className="font-bold mb-2">Statistiques des notes</h4>
                  <div className="space-y-2">
                    {ratingDistribution?.map((item) => (
                      <div key={item.rating} className="flex items-center">
                        <span className="w-24">
                          {item.rating} étoile{item.rating > 1 ? "s" : ""}
                        </span>
                        <div className="flex-1 ml-2">
                          <div
                            className="bg-blue-500 h-4 rounded"
                            style={{
                              width: `${
                                (item.count /
                                  Math.max(
                                    ...(ratingDistribution?.map(
                                      (r) => r.count
                                    ) || [1]) // Handle empty array case for max
                                  )) *
                                100
                              }%`,
                            }}
                          ></div>
                        </div>
                        <span className="w-12 text-right">{item.count}</span>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default HomePage;
